export const Rules = (tipo, requerido, campo)  => {
    const rules = {};

    const globlal_rule = {
        required: requerido ? true : false,
        message: `El campo ${campo} es requerido`
    }

    const type_rule = null;
    if(tipo) {
        type_rule = {
            type: `${tipo}`,
            message: `El ${tipo} no es valido`
        }
    }

    return rules;
}

