export class Rules {

    minLenght(min) {
        const minLenght_rule = {
            min: `${min}`,
            message: `Es necesario ingresar mínimo ${min} caracteres`
        };

        return minLenght_rule;
    }

    maxLenght(max) {
        const maxLenght_rule = {
            required: `${max}`,
            message: `Es necesario ingresar máximo ${max} caracteres`
        };

        return maxLenght_rule;
    }

    required(campo) {
        const required_rule = {
            required: true,
            message: `El campo ${campo} es requerido`
        };

        return required_rule;
    }

    type_email() {
        const email_rule = {
            type: 'email',
            message: `El correo no es valido`
        }

        return email_rule;
    }
}

