export const Rules = (es_requerido, campo, tipo)  => {
    const globlal_rule = {
        required: es_requerido ? true : false,
        message: `El campo ${campo} es requerido`
    }

    let type_rule = null;
    if(tipo) {
        type_rule = {
            type: `${tipo}`,
            message: `El ${tipo} no es valido`
        }
    }

    let rules = [];
    rules.push.apply(globlal_rule, type_rule);

    return rules;
}

