import React from 'react'
import { Form, Input, Button } from 'antd';
import { Rules } from "../../helpers/ValidationsForm";
import { UserOutlined } from '@ant-design/icons';

import "./Login.scss";

function Login() {

    const [form] = Form.useForm();

    const { minLenght, required  } = Rules;

    console.log('min', minLenght(8));

    return (
        <div className="container d-flex justify-content-center align-items-center">
            <div className="row container-login">
                <div className="col-12 d-flex justify-content-center">
                    <picture>
                        <source media="(min-width:500px)" srcSet={require('../../assets/Logo.png')} />
                        <source media="(min-width:320px)" srcSet={require('../../assets/imgs-small/Logo_small.png')} />
                        <img className="img-fluid mx-auto" src={require('../../assets/Logo.png')} alt="Logo empresa" />
                    </picture>
                </div>
                <div className="col-12">
                    <Form form={form} name="login" layout="vertical">
                        <Form.Item name="usuario" label="Usuario" rules={[]}>
                            <Input placeholder="Ingresa tu usuario" prefix={<UserOutlined />}></Input>
                        </Form.Item>

                        <Form.Item name="contrasena" label="Contraseña" rules={[]}>
                            <Input.Password placeholder="Ingresa tu contraseña" />
                        </Form.Item>
                    </Form>
                </div>
            </div>
        </div>
    )
}

export default Login
